mapper.select:
select concat(ifnull(sum(DistributeProfit)+sum(Profit) , 0) , '') as Amount from tab_salesman_profit where TradeUserID in (
select ID from tab_loginuser where (ThreeLevel=#{merchantID} or TwoLevel=#{merchantID}
or ID=#{merchantID})) and SalesManID=#{UserID}

{call tixian(
   #{in_loginID , mode=IN,jdbcType=VARCHAR},
	#{in_amount ,mode=IN,jdbcType=VARCHAR},
	#{in_termSerno , mode=IN,jdbcType=VARCHAR},
	#{in_txType , mode=IN,jdbcType=VARCHAR},
	#{in_accountNo , mode=IN,jdbcType=VARCHAR},
	#{ret , mode=OUT,jdbcType=INTEGER}
	)
}

mapper.update:
update tab_loginuser set HandheldIDPhoto=#{HandheldIDPhoto} , IDCardFrontPhoto=#{IDCardFrontPhoto},
IDCardReversePhoto=#{IDCardReversePhoto} ,  BankCardPhoto=#{BankCardPhoto} , BusinessPhoto=#{BusinessPhoto} ,
		 PhotoStatus=1 where LoginID=#{loginID}
